% CreateDataFile.mfunction err = CreateDataFile(Data)%Check acceleration signif Data.RunType == 1    Gradient = 'DIS';else %Data.RunType == 2    Gradient = 'LOC';end%Get DateData.Date = fix(clock);Data.Folder = [ 'Data' filesep ];Data.Filename = sprintf('%s-%04d-%02d-%02d-%02d-%02d-%s-%04d-%04d.mat', ...    Data.SubjectID, Data.Date(1), Data.Date(2), Data.Date(3), Data.Date(4), Data.Date(5), Gradient, Data.StimulusDuration*1000, Data.StimulusDistance);Datahome = pwd;err = 0;try,	cd(Data.Folder);    save(Data.Filename, 'Data', '-mat7-binary');	cd(home);catch,    err = 1;	cd(home);    fprintf('Cannot create data file "%s"\n', [ Data.Folder Data.Filename ]);    return;end[fid, mesg] = fopen([ Data.Folder Data.Filename ], 'r');if fid >= 0    fclose(fid);else    err = 2;	cd(home);    fprintf('%s\n', mesg);    fprintf('"%s"\n', [ Data.Folder Data.Filename ]);    return;end